import React, { useState, useEffect } from 'react';
import TaskForm from './TaskForm';
import TaskList from './TaskList';
import './index.css';

function App() {
  const [tasks, setTasks] = useState(JSON.parse(localStorage.getItem('tasksData')) || []);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [editTaskIndex, setEditTaskIndex] = useState(null);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    isImportant: false,
    status: 'pending',
  });

  const openModal = () => {
    setIsModalOpen(true);
    setFormData({
      title: '',
      description: '',
      isImportant: false,
      status: 'pending',
    });
    setEditMode(false);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setEditMode(false);
  };

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  const saveTask = (e) => {
    e.preventDefault();
    if (editMode) {
      const updatedTasks = tasks.map((task, index) =>
        index === editTaskIndex ? formData : task
      );
      setTasks(updatedTasks);
      localStorage.setItem('tasksData', JSON.stringify(updatedTasks));
    } else {
      const updatedTasks = [...tasks, formData];
      setTasks(updatedTasks);
      localStorage.setItem('tasksData', JSON.stringify(updatedTasks));
    }
    closeModal();
  };

  const editTask = (index) => {
    const taskToEdit = tasks[index];
    setFormData(taskToEdit);
    setEditMode(true);
    setEditTaskIndex(index);
    setIsModalOpen(true);
  };

  const deleteTask = (index) => {
    const updatedTasks = tasks.filter((_, i) => i !== index);
    setTasks(updatedTasks);
    localStorage.setItem('tasksData', JSON.stringify(updatedTasks));
  };

  useEffect(() => {
    localStorage.setItem('tasksData', JSON.stringify(tasks));
  }, [tasks]);

  return (
    <div className="App">
      <h1>To-Do List</h1>
      <button onClick={openModal}>Create Task</button>
      <div className="task-list">
        {tasks.map((task, index) => (
          <div className="task-item" key={index}>
            <h3>{task.title}</h3>
            <p>{task.description}</p>
            <p>Important: {task.isImportant ? 'Yes' : 'No'}</p>
            <p>Status: {task.status}</p>
            <button onClick={() => editTask(index)}>Edit</button>
            <button onClick={() => deleteTask(index)}>Delete</button>
          </div>
        ))}
      </div>

      {isModalOpen && (
        <div className="modal">
          <form onSubmit={saveTask}>
            <h2>{editMode ? 'Edit Task' : 'Create Task'}</h2>
            <label>Title:</label>
            <input
              type="text"
              name="title"
              value={formData.title}
              onChange={handleInputChange}
              required
            />
            <label>Description:</label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleInputChange}
              required
            />
            <label>
              <input
                type="checkbox"
                name="isImportant"
                checked={formData.isImportant}
                onChange={handleInputChange}
              />
              Important
            </label>
            <label>Status:</label>
            <select
              name="status"
              value={formData.status}
              onChange={handleInputChange}
            >
              <option value="pending">Pending</option>
              <option value="completed">Completed</option>
            </select>
            <button type="submit">{editMode ? 'Update Task' : 'Add Task'}</button>
            <button type="button" onClick={closeModal}>Cancel</button>
          </form>
        </div>
      )}
    </div>
  );
}

export default App;
